import React from "react";

const JobBoardComponent = ({ job }) => {
  const langAndTools = [];
  if (job.tools) {
    langAndTools.push(...job.tools);
  }
  if (job.languages) {
    langAndTools.push(...job.languages);
  }
  return (
    <div className="flex bg-white shadow-md m-4 p-6 rounded">
      <div>
        <img className="h-12 w-12" src={job.logo} alt={job.company} />
      </div>
      <div className="flex flex-col justify-between ml-4">
        <h3 className="font-bold text-blue-500">
          {job.company}
          {isNew && <span>new</span>}
          {isFeatured && <span>featured</span>}
        </h3>
        <h2 className="font-bold text-xl">{job.position}</h2>
        <p className="text-gray-700">
          {job.postedAt} · {job.contract} · {job.location}
        </p>
      </div>
      <div className="flex items-center justify-end ml-auto">
        {langAndTools
          ? langAndTools.map((langAndTool) => (
              <span className="text-blue-500 bg-blue-100 font-bold m-2 p-2 rounded">
                {langAndTool}
              </span>
            ))
          : ""}
      </div>
    </div>
  );
};

export default JobBoardComponent;
