import "./App.css";
import JobBoardComponent from "./components/JobBoardComponent";
import { useState, useEffect } from "react";
import data from "./assets/data.json";
import ReactPaginate from "react-paginate";
function App() {
  const [jobs, setJobs] = useState(data.slice(0, 30));
  const [filters, setFilters] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);

  const jobsPerPage = 5;
  const pagesVisited = pageNumber * jobsPerPage;

  const changePage = ({ selected }) => {
    setPageNumber(selected);
  };

  const filterFunc = ({ role, level, tools, languages }) => {
    if (filters.length === 0) {
      return true;
    }
    const tags = [role, level];
    if (tools) {
      tags.push(...tools);
    }
    if (languages) {
      tags.push(...languages);
    }
    return tags.some((tag) => filters.includes(tag));
  };
  const filteredJobs = jobs.filter(filterFunc);
  const handleTagClick = (tag) => {
    if (filters.includes(tag)) return;
    setFilters([...filters, tag]);
  };
  const handleFilterClick = (passedFilter) => {
    setFilters(filters.filter((f) => f !== passedFilter));
  };

  const pageCount = Math.ceil(jobs.length / jobsPerPage);

  useEffect(() => {
    setJobs(data);
    return () => {};
  }, []);
  const displayJobs = jobs
    .slice(pagesVisited, pagesVisited + jobsPerPage)
    .map((job) => {
      return (
        <div className="user">
          {
            // filteredJobs.map((x) => (
            //   <JobBoardComponent
            //     job={x}
            //     key={x.id}
            //     handleTagClick={handleTagClick}
            //   />
            // ))
            // <JobBoardComponent
            //   job={job}
            //   key={job.id}
            //   handleTagClick={handleTagClick}
            // />
          }
        </div>
      );
    });

  return (
    <div className="App">
      <div className="flex bg-white shadow-md my-16 mx-10 p-6 rounded">
        {filters.length > 0 &&
          filters.map((filter) => (
            <span
              onClick={() => handleFilterClick(filter)}
              className="text-blue-500 bg-blue-100 font-bold m-2 p-2 rounded"
            >
              {filter}
            </span>
          ))}
      </div>
      {jobs.length === 0 ? (
        <p>jobs are fetching</p>
      ) : (
        filteredJobs.map((job) => (
          <JobBoardComponent
            job={job}
            key={job.id}
            handleTagClick={handleTagClick}
          />
        ))
      )}
      {/* {displayJobs}
      <ReactPaginate
        previousLabel="Previous"
        nextLabel="Next"
        pageCount={pageCount}
        onPageChange={changePage}
        containerClassName={"paginationBttns"}
        previousLinkClassName={"pretviousBttn"}
        nextLinkClassName={"nextBttn"}
        disabledClassName={"paginationDisabled"}
        activeClassName={"paginationActive"} */}
      />
    </div>
  );
}

export default App;
